import React from 'react';
import Toggle from './Toggle';
import useDarkMode from 'use-dark-mode';
import './ContentPanel.css'

const DarkModeToggle = () => {
  const darkMode = useDarkMode(false);

  return (
    <div className="dark-mode-toggle">
      {/* <button type="button" onClick={darkMode.disable}>
        ☀
      </button>
      <Toggle checked={darkMode.value} onChange={darkMode.toggle} />
      <button type="button" onClick={darkMode.enable}>
        ☾
      </button> */}
    <div class='toggleContainer' onClick={darkMode.toggle}>
      <button type="button" onClick={darkMode.disable}> ☀ </button>
      <img src="https://image.flaticon.com/icons/svg/2033/2033921.svg" width="224" height="224" alt="Moon free icon" title="Moon free icon"/>
    </div>
    </div>

  );
};

export default DarkModeToggle;
