                    // <div className='content-column'>
                    //     <ContentPanel >		
                    //         <section id="home">	
                    //             <HomeScreen />
                    //         </section>
                    //         <section id="about">	
                    //             <AboutMe/>
                    //         </section>
                    //         <section id="skills">	
                    //             <Skills/>
