                    //             <Blog/>
                    //         </section>
                    //         <section id="contact">	
                    //             <Contact/>
                    //         </section>
                    //     </ContentPanel>
                    // </div>


            //         {/* </div> */}
            //     </div>
            // </div>
        )
    }
}

export default Main;
